const images=document.querySelectorAll(".slider .slider-line img"),sliderLine=document.querySelector(".slider-line");let width,count=0;function init(){console.log("resize"),width=document.querySelector(".slider").offsetWidth,console.log(width),sliderLine.style.width=width*images.length+"px",images.forEach(e=>{e.style.width=width+"px",e.style.height="auto"}),rollSlider()}function rollSlider(){sliderLine.style.transform="translate(-"+count*width+"px)"}window.addEventListener("resize",init),init(),document.querySelector(".slider-prev").addEventListener("click",(function(){count--,count<0&&(count=images.length-1),rollSlider()})),document.querySelector(".slider-next").addEventListener("click",(function(){count++,count>=images.length&&(count=0),rollSlider()}));